import pandas as pd
import numpy
import re
import pickle
from nltk.corpus import stopwords
from nltk.stem.porter import PorterStemmer
from sklearn.feature_extraction.text import TfidfVectorizer
from sklearn.model_selection import train_test_split
from sklearn.linear_model import LogisticRegression
from sklearn.metrics import accuracy_score
from sklearn.svm import LinearSVC
from sklearn.naive_bayes import GaussianNB
from sklearn.ensemble import RandomForestClassifier

import nltk

nltk.download('stopwords')
print(stopwords.words('english'))

# data preprocessing
# loading dataset into pandas dataframe
train_test_dataset1 = pd.read_csv('train_test.csv')
train_test_dataset = train_test_dataset1.sample(frac=1)
x = train_test_dataset.shape
# print("Rows and columns in dataset :")
# print(x)
print(train_test_dataset.head())

# counting the number of missing values in the dataset
print(train_test_dataset.isnull().sum())
train_test_dataset.dtypes

# replacing null values with empty string
train_test_dataset = train_test_dataset.fillna('')
train_test_dataset.dtypes

# Stemming
port_stem = PorterStemmer()


def stemming(content):
    stemmed_content = re.sub('[^a-zA-Z]', ' ', content)
    stemmed_content = stemmed_content.lower()
    stemmed_content = stemmed_content.split()
    stemmed_content = [port_stem.stem(word) for word in stemmed_content if not word in stopwords.words('english')]
    stemmed_content = ' '.join(stemmed_content)
    return stemmed_content


train_test_dataset['comment'] = train_test_dataset['comment'].apply(stemming)
print(train_test_dataset['comment'])

# converting textual data into numerical data
vectorizer = TfidfVectorizer()
vectorizer.fit(train_test_dataset['comment'].values)

X = vectorizer.transform(train_test_dataset['comment'].values)
print(X)

# Running different Models
train_test_dataset.label.unique()
train_test_dataset['label'] = train_test_dataset['label'].replace([''],'0')
train_test_dataset['label'] = train_test_dataset['label'].replace(['0'], 0)
train_test_dataset['label'] = train_test_dataset['label'].replace(['1'], 1)
train_test_dataset.label.unique()

Y = train_test_dataset['label'].values
X_train, X_test, Y_train, Y_test = train_test_split(X, Y, test_size=0.2, stratify=Y, random_state=2)
model = LogisticRegression(solver='lbfgs', max_iter=5000)  # training =70.138  testing = 67.253
# model = LinearSVC()     # training = 72.0558  testing = 66.58686
# model = RandomForestClassifier(n_estimators=100)     # training =   testing =
# model = GaussianNB()      # training =   testing =
model.fit(X_train, Y_train)

X_train_prediction = model.predict(X_train)
training_data_accuracy = accuracy_score(X_train_prediction, Y_train)

print('Accuracy score of training data =', training_data_accuracy)

X_test_prediction = model.predict(X_test)
testing_data_accuracy = accuracy_score(X_test_prediction, Y_test)
print('Accuracy score of testing data =', testing_data_accuracy)

# saving the model in disk for later use
with open('model.pkl', 'wb') as f:
    pickle.dump(model, f)

# saving the model in disk for later use
with open('tfidf.pkl', 'wb') as f:
    pickle.dump(vectorizer, f)
